typedef unsigned d;
d g;
int h[] = {0x92A1D2CDL, 0x92A1D2CDL, 0x92A1D2CDL};
static d i = 0x06D7L;
d fn2() {
  for (;;) {
    c q = 0xC3L;
    int r[5][9][5];
    if (h[2])
      return q;
    g = r[i][i][i];
  }
}
int main() { fn2(); }
