  struct c l = {24, j.int_field_e, 165};
  l.int_field_d = j.int_field_e;
  int int_var_b2 = j.int_field_h && l.int_field_d;
  signed int_var_p =
      l.int_field_d | -(l.int_field_f && l.int_field_d) - int_var_b2;
  i.int_field_g = int_var_p;
